package practise;

public class EvenandOddinArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a[]= {1,2,3,4,5,6,7,8,9,1};
		
		for (int i:a) {
			
			if(i%2==0) {
				System.out.println("even");
			}else {
				System.out.println("odd");
			}
		}
		

	}

}
